function html_entity_decode(value) {    var replaceArray = ['á', 'Á', 'à', 'À', 'â', 'Â', 'ä', 'Ä',                          'ã', 'Ã', 'ç', 'Ç', 'é', 'É', 'è', 'È',                          'ê', 'Ê', 'ë', 'Ë', 'í', 'Í', 'ì', 'Ì',                          'î', 'Î', 'ï', 'Ï', 'ó', 'Ó', 'ò', 'Ò',                          'ô', 'Ô', 'ö', 'Ö', 'ú', 'Ú', 'ù', 'Ù',                          'û', 'Û', 'ü', 'Ü','ñ'];	var findArray = ['&aacute;', '&Aacute;', '&agrave;', '&Agrave;',                          '&acirc;', '&Acirc;', '&auml;', '&Auml;',                          '&atilde;', '&Atilde;', '&ccedil;', '&Ccedil;',                          '&eacute;', '&Eacute;', '&egrave;', '&Egrave;',                          '&ecirc;', '&Ecirc;', '&euml;', '&Euml;',                          '&iacute;', '&Iacute;', '&igrave;', '&Igrave;',                          '&icirc;', '&Icirc;', '&iuml;', '&Iuml;',                          '&oacute;', '&Oacute;', '&ograve;', '&Ograve;',                          '&ocirc;', '&Ocirc;', '&ouml;', '&Ouml;',                          '&uacute;', '&Uacute;', '&ugrave;', '&Ugrave;',                          '&ucirc;', '&Ucirc;', '&uuml;', '&Uuml;', '&ntilde;'];						  	for(var i = 0; i < findArray.length; i++){		value = value.replace(new RegExp(findArray[i],'g'),replaceArray[i]);	}		return value;     }    //#33CCCC, #C5AAEE, #B8E1E1, #E4DAB4, #B4D1E4, #A3A3F5, #333333 